import React, { useEffect } from 'react';
import fullpage from 'fullpage.js';
import Section1 from './components/Section1';
import Section2 from './components/Section2';
import Section3 from './components/Section3';
import './styles/reset.scss';
import './styles/App.scss';

const App = () => {
    useEffect(() => {
        new fullpage('#fullpage', {
            autoScrolling: true,
            scrollHorizontally: true,
            scrollingSpeed: 700, // Швидкість прокрутки
            navigation: true, // Показує навігацію
            navigationPosition: 'right',
            controlArrows: true, // Стрілки для прокрутки
            verticalCentered: true,
            sectionSelector: '.section',
            scrollOverflow: true, // Включає прокрутку для переповнених секцій
            responsiveWidth: 768, // Вимикає autoScrolling на мобільних пристроях
        });
    }, []);

    return (
        <div id="fullpage">
            <div className="section"><Section1 /></div>
            <div className="section"><Section2 /></div>
            <div className="section"><Section3 /></div>
            {/* Додайте інші секції тут */}
        </div>
    );
};

export default App;
